import React, { Component, ChangeEvent } from 'react';

const initialState = {
    login: true,
    username: '',
    password: '',
    email: ''
}
type LoginState = {
    login: boolean,
    username: string,
    password: string,
    email: string
}

export default class Login extends Component<{}, LoginState> {
    readonly state: LoginState = initialState

    handleChange(value: string, field: string) {
        this.setState(prevState => ({
            ...prevState,
            [field]: value
        }), () => console.log(this.state))
    }

    toggleLogin() {
        this.setState({ login: !this.state.login })
    }

    render() {
        return (
            <div>
                <h1>{this.state.login ? 'Login' : 'Register'}</h1>
                <button onClick={() => this.toggleLogin()}>
                    {this.state.login ? 'Register' : 'Login'}
                </button>

                <form>
                    <label>
                        Username
                        <input type="text" value={this.state.username} onChange={e => this.handleChange(e.currentTarget.value, 'username')} />
                    </label>

                    <br />

                    <label>
                        Password
                        <input type="password" value={this.state.password} onChange={e => this.handleChange(e.currentTarget.value, 'password')} />
                    </label>

                    <br />

                    {!this.state.login &&
                        <label>
                            Password
                        <input type="password" value={this.state.password} onChange={e => this.handleChange(e.currentTarget.value, 'password')} />
                        </label>
                    }

                    <br />

                    <input type="submit" value="Submit" />
                </form>
            </div>
        )
    }
}
