import React, { Component, ChangeEvent, FormEvent } from 'react';

import {
    Container,
    TextField,
    Button,
    makeStyles
} from '@material-ui/core'

const initialState = {
    login: true,
    username: '',
    password: '',
    email: ''
}
type LoginState = {
    login: boolean,
    username: string,
    password: string,
    email: string
}

const useStyles = makeStyles({
    textInput: {
        marginBottom: 2
    }
})

export default class Login extends Component<{}, LoginState> {
    readonly state: LoginState = initialState
    readonly classes = useStyles()

    handleChange(value: string, field: string) {
        this.setState(prevState => ({
            ...prevState,
            [field]: value
        }))
    }

    handleSubmit(event: FormEvent<HTMLFormElement>) {
        event.preventDefault()
        console.log(this.state)
        alert('submitted')
    }

    toggleLogin() {
        this.setState({ login: !this.state.login })
    }

    render() {
        return (
            <Container maxWidth="sm">
                <h1>{this.state.login ? 'Login' : 'Register'}</h1>
                <Button variant='contained' onClick={() => this.toggleLogin()}>
                    {this.state.login ? 'Register' : 'Login'}
                </Button>

                <form onSubmit={(e) => this.handleSubmit(e)}>
                    <TextField
                        id='username'
                        label='Username'
                        className={this.classes.textInput}
                        value={this.state.username}
                        onChange={e => this.handleChange(e.currentTarget.value, 'username')}
                    />


                    {!this.state.login &&
                        <TextField
                            id='email'
                            label='Email'
                            value={this.state.email}
                            onChange={e => this.handleChange(e.currentTarget.value, 'email')}
                        />
                    }

                    <TextField
                        id='password'
                        label='Password'
                        type='password'
                        value={this.state.password}
                        onChange={e => this.handleChange(e.currentTarget.value, 'password')}
                    />

                    <br />

                    <Button
                        variant='contained'
                        color='primary'
                        type='submit'
                        value='Submit'
                    >
                        Submit
                    </Button>
                </form>
            </Container>
        )
    }
}
